$(document).ready(function(){$menuBtn=$(".menuBtn");$nav=$("nav");$fill=nthOfType(".fill",4);$mask=$(".mask");if(window.innerWidth>800){}else{}startup($fill,$mask);$menuBtn.on("click",function(){openNav($nav)})});function openNav(n){n.toggleClass("selected")}function nthOfType(n,t){var e=[];for(var a=1;a<=t;a++){e.push($(n+":nth-of-type("+a+")"))}return e}function startup(n,t){for(var e=0;e<n.length;e++){n[e].toggleClass("started")}t.toggleClass("started")}