$(document).ready(function(){$window=$(window);$body=$("body");$menuBtn=$(".menuBtn");$nav=$("nav");$fill=nthOfType(".fill",4);$mask=$(".mask");$filler=nthOfType(".filler",4);$masking=$(".masking");$link=$(".link a");$start=$("#start");$loader=$("#loader");$path=nthOfType("path",4);if(window.innerWidth>800){}else{}startup($fill,$mask,$loader);$menuBtn.on("click",function(){openNav($nav)});$link.on("click",function(){openNav($nav)});var t=$start.offset().top;var n=$window.height();var e=.65;var a=t-n*e;var o=$body.height()-n-5;$(window).on("scroll",function(){var t=window.pageYOffset;var n=t>a;var e=o<=t&&!n;if(n||e){seeGetStarted($filler,$masking)}})});function openNav(t){t.toggleClass("selected")}function nthOfType(t,n){var e=[];for(var a=1;a<=n;a++){e.push($(t+":nth-of-type("+a+")"))}return e}function startup(t,n,e){setTimeout(function(){e.toggleClass("started");for(var a=0;a<t.length;a++){t[a].toggleClass("started")}n.toggleClass("started")},1e3);setTimeout(function(){e.toggleClass("finishedStarting")},2e3)}function seeGetStarted(t,n){for(var e=0;e<t.length;e++){t[e].addClass("started")}n.addClass("started")}