$(document).ready(function(){$menuBtn=$(".menuBtn");$nav=$("nav");$fill=nthOfType(".fill",4);$mask=$(".mask");$filler=nthOfType(".filler",4);$masking=$(".masking");if(window.innerWidth>800){}else{}startup($fill,$mask);seeGetStarted($filler,$masking);$menuBtn.on("click",function(){openNav($nav)})});function openNav(t){t.toggleClass("selected")}function nthOfType(t,e){var n=[];for(var a=1;a<=e;a++){n.push($(t+":nth-of-type("+a+")"))}return n}function startup(t,e){for(var n=0;n<t.length;n++){t[n].toggleClass("started")}e.toggleClass("started")}function seeGetStarted(t,e){for(var n=0;n<t.length;n++){t[n].toggleClass("started")}e.toggleClass("started")}