$(document).ready(function(){$window=$(window);$body=$("body");$menuBtn=$(".menuBtn");$nav=$("nav");$fill=nthOfType(".fill",4);$mask=$(".mask");$filler=nthOfType(".filler",4);$masking=$(".masking");$link=$(".link a");$start=$("#start");if(window.innerWidth>800){}else{}startup($fill,$mask);$menuBtn.on("click",function(){openNav($nav)});$link.on("click",function(){openNav($nav)});var n=$start.offset().top;var t=$window.height();var a=.65;var e=n-t*a;var i=$body.height()-t-5;$(window).on("scroll",function(){var n=window.pageYOffset;var t=n>e;var a=i<=n&&!t;if(t||a){seeGetStarted($filler,$masking)}})});function openNav(n){n.toggleClass("selected")}function nthOfType(n,t){var a=[];for(var e=1;e<=t;e++){a.push($(n+":nth-of-type("+e+")"))}return a}function startup(n,t){for(var a=0;a<n.length;a++){n[a].toggleClass("started")}t.toggleClass("started")}function seeGetStarted(n,t){for(var a=0;a<n.length;a++){n[a].addClass("started")}t.addClass("started")}